  name : 'counter',
  initialState,
  reducers : {
    increment(state){
      state.counter++ 
    },
    decrement(state){
      state.counter--
    },
    increase(state,action){
      state.counter = state.counter + action.amount;
    },
    toggleCounter(state){
      state.showCounter = !state.showCounter;
    }
  }
})


const store = configureStore({
  reducer : counterSlice.reducer
})

